import cdk.ecr.deployment.*;

Object image = DockerImageAsset.Builder.create(this, "CDKDockerImage")
        .directory(path.join(__dirname, "docker"))
        .build();

ECRDeployment.Builder.create(this, "DeployDockerImage")
        .src(new DockerImageName(image.getImageUri()))
        .dest(new DockerImageName(String.format("%s.dkr.ecr.us-west-2.amazonaws.com/test:nginx", cdk.getAws().getACCOUNT_ID())))
        .build();

